INSERT INTO `hirek` (`id`, `oldal_url`, `oldal_cim`, `oldal_kulcsszavak`, `oldal_leiras`, `oldal_tartalom`, `publikalas_datuma`, `szerzo`, `kep`, `k_id`) VALUES
(1, 'Dalok-%C3%A9s-%C3%A1ri%C3%A1k-a-barokkt%C3%B3l-napjainkig', 'Dalok és áriák a barokktól napjainkig', 'dalok, ariak', 'Ambruzs Berill, Józan Vivien és Sipos Katalin estje\r\n2018. január 3. (szerda) 18 óra\r\nZenei Gyűjtemény; Budapest VIII., Ötpacsirta utca 4.', '<h1>Dalok és áriák a barokktól napjainkig</h1>\r\n\r\n<p><img alt=\"\" src=\"http://www.fszek.hu/_Images/konyvtaraink/kozponti_konyvtar/zenei_gyujtemeny/000036675_jozan_vivien.jpg.jpg_orig.jpg\"></p>\r\n\r\n<h2><strong>Műsor</strong></h2>\r\n\r\n<p><br>\r\n<strong>Obradors:</strong> El Vito<br>\r\n<strong>Csajkovszkij:</strong> Nem mondok neked semmit<br>\r\n<strong>Verdi:</strong> Non t&#39;accostare all&#39;urna<br>\r\n<strong>Brahms:</strong> Salamander<br>\r\n<strong>Vivaldi:</strong> Quia respexit<br>\r\n<strong>Haydn:</strong> La canterina - „Non v&#39;e chi mi aiuta”<br>\r\n<strong>Schubert:</strong> Seligkeit<br>\r\n<strong>Vivaldi:</strong> Un certo non so che<br>\r\n<strong>Händel:</strong> Oh sleep<br>\r\n<strong>Rachmaninov:</strong> Siren<br>\r\n<strong>Kodály Zoltán:</strong> Jöjj te hozzám<br>\r\n<strong>Puccini:</strong> E l’uccellino<br>\r\n<strong>Tosti:</strong> Malia<br>\r\n<strong>Kodály:</strong> Mezei dal<br>\r\n<strong>Nielsen:</strong> Sommersang<br>\r\n<strong>Liszt:</strong> Vergiftet sind meine Lieder<br>\r\n<strong>Csajkovszkij:</strong> Hazatérés<br>\r\n<strong>Berg:</strong> Am Abend<br>\r\n<strong>Fekete Gyula:</strong> Két szerető dalja<br>\r\n<strong>W.A.Mozart:</strong> Cosí fan tutte – „Una donna a quindici anni”<br>\r\n<br>\r\nZongorán közreműködik: <strong>Virág Emese</strong></p>\r\n\r\n<p>        A Liszt Ferenc Zeneművészeti Egyetem klasszikus ének szakának másodéves hallgatója Kertesi Ingrid osztályában, Virág Emese növendékeként. Zenei előtanulmányait Hatvanban, a Kocsis Albert Zeneiskolában kezdte furulyán, amit magánének követett, párhuzamosan a zongorával. A Bartók Béla Zeneművészeti Szakközépiskola OKJ szakának elvégzése után, Deliné Soós Ágnes felkészítésével lett zeneakadémista. Immáron másodjára szerepelhetett a berlini Hans Eisler Zeneakadémia ének szakos hallgatóival idén Berlinben, egy koprodukció keretén belül, melyben operarészleteket rendeztek újra és adtak elő. A IV. Székely Mihály Énekes Találkozó III. díjasa. Számos felkérés mellett büszke arra, hogy a Hatvani Szimfonikus Zenekarral, Tótpál Szilvia operaénekessel, illetve a Corelli Consort barokk kamarazenekarral is egy színpadra állhatott.</p>\r\n', 1249794000, 'Józan Vivien', 'qDDY9TyW65AkXzcd.png', 1),
(7, 'Cziffra-k%C3%B3d', 'Cziffra-kód', 'Cziffra-kód', 'Zenei és művelődéstörténeti vetélkedő Liszt Ferencről és koráról. \r\nFelhívás 14–18 éves diákok részére. \r\nElső forduló: 2017. december 12. és 2018. február 7. között. \r\nDöntő: 2018. február 14. (szerda) 15 óra, MOM Kulturális Központ', '<h1>Cziffra-kód</h1>\r\n\r\n<p>Zenei és művelődéstörténeti vetélkedő Liszt Ferencről és koráról. <br>\r\nFelhívás 14–18 éves diákok részére. <br>\r\nElső forduló: 2017. december 12. és 2018. február 7. között. <br>\r\nDöntő: 2018. február 14. (szerda) 15 óra, MOM Kulturális Központ</p>\r\n\r\n<p> </p>\r\n\r\n<table align=\"right\" border=\"0\" cellpadding=\"0\" cellspacing=\"4\">\r\n <tbody>\r\n  <tr>\r\n   <td><a href=\"http://www.fszek.hu/_Images/konyvtaraink/kozponti_konyvtar/rendezvenyek/000036631_cziffrakod18v1.jpg.jpg_orig.jpg\"><img alt=\"Forrás: Cziffra György Fesztivál\" src=\"http://www.fszek.hu/_Images/konyvtaraink/kozponti_konyvtar/rendezvenyek/000036631_cziffrakod18v1.jpg.jpg_orig.jpg\"></a></td>\r\n  </tr>\r\n  <tr>\r\n   <td>Forrás: Cziffra György Fesztivál</td>\r\n  </tr>\r\n </tbody>\r\n</table>\r\n\r\n<p>A <a href=\"http://cziffrafesztival.hu/cziffra-kod/\" target=\"_blank\">Cziffra György Fesztivál</a> a MOM Kulturális Központ, a Fővárosi Szabó Ervin Könyvtár és az ELTE BTK Zenei Tanszéke közreműködésével <em>zenei és művelődéstörténeti vetélkedőt</em> hirdet <strong>Cziffra-kód</strong> elnevezéssel. <br>\r\n<br>\r\nFelhívás 14–18 éves diákok részére. <br>\r\nElső forduló: 2017. december 12. és 2018. február 7. között. <br>\r\nDöntő: 2018. február 14. (szerda) 15 óra, MOM Kulturális Központ <br>\r\n<br>\r\n<strong>Cziffra-kód – Zenei és művelődéstörténeti vetélkedő Liszt Ferencről és koráról</strong>. <br>\r\n<br>\r\n<strong>Résztvevők köre</strong>: <br>\r\n3 fős csapatok: budapesti, 14–18 év közötti középiskolás diákok,</p>\r\n\r\n<ul>\r\n <li>akik érdeklődnek a 19. század pesti művészvilága iránt,</li>\r\n <li>akik mindig többet és többet akarnak tudni,</li>\r\n <li>akiket lenyűgöz Liszt Ferenc munkássága,</li>\r\n <li>akik szeretik a furfangos logikai játékokat, és nem okoz számukra gondot megtalálni az információt az interneten vagy könyvekben,</li>\r\n <li>akik otthonosan mozognak a telefonos alkalmazások világában (a játék során a Munzee applikációra lesz szükség),</li>\r\n <li>és akik szívesen barangolnának Budapest utcáin, kódot fejtve.</li>\r\n</ul>\r\n\r\n<p><br>\r\n<strong>Regisztráció</strong>: <a href=\"mailto:cziffrakod@cziffrafesztival.hu?subject=regisztráció\" target=\"_blank\">cziffrakod@cziffrafesztival.hu</a> <br>\r\n(A regisztrációhoz szükséges adatok: csapatnév, kapcsolattartó személy neve és e-mail címe. <br>\r\nTovábbi információ: Bándoli Katalin, 411-5010, <a href=\"mailto:bandoli@fszek.hu?subject=cziffra-kód\" target=\"_blank\">bandoli@fszek.hu</a>) <br>\r\n<br>\r\n<strong>A vetélkedő menete</strong>: <br>\r\n<br>\r\n<strong><em>Első forduló</em></strong>: <br>\r\n<strong>2017. december 12. és 2018. február 7. között</strong> <br>\r\n19. században játszódó, rejtélyes bűnügyet feltáró minikrimit kapnak a csapatok e-mailben a nevezés után. A történetben kódok helyettesítenek bizonyos szavakat, így csak akkor lebben fel a fátyol a bűntett egyes részleteiről, ha megfejtik a játékosok a kódokat. Ezekhez zenei, irodalmi, képzőművészeti, tudománytörténeti vonatkozású kérdések kapcsolódnak, melyek információkereséssel vagy logikai úton oldhatók meg. A zenei részek középpontjában Liszt Ferenc életműve áll. <br>\r\nA teljes történetet 2018. február 7., éjfélig kell visszaküldeni. A döntőbe jutott 6 csapatot 2018. február 8-án értesítjük. <br>\r\n<br>\r\n<strong><em>Döntő</em></strong>: a 2018. február 14-én tartandó döntőbe a legtöbb pontszámot elérő hat csapat kerülhet be. A döntőben is a kreativitás, kommunikációs készség, a gyors információkeresés és a csapatszellem lesz a legfontosabb. A döntő szünetében a zenében rejlő kreativitást az improvizáló zongoraművész, Balázs János mutatja be. <br>\r\n<br>\r\nA zsűri elnöke: <strong>Balázs János</strong> zongoraművész, a Cziffra Fesztivál művészeti vezetője. <br>\r\n<br>\r\nKövessék <a href=\"https://www.facebook.com/Cziffra-kód-361913994270443/\" target=\"_blank\">Facebook</a>-on a Cziffra-kód nevű oldalt, ahol posztokkal segítjük a csapatok munkáját! </p>\r\n\r\n<p><img alt=\"Támogatók\" src=\"http://www.fszek.hu/_Images/konyvtaraink/kozponti_konyvtar/rendezvenyek/000036632_czffszek.jpg.jpg_orig.jpg\"> </p>\r\n', 1249794000, 'Német István', 'GhXajk9knR5kjVV2.jpg', 3),
(8, 'Biblioter%C3%A1pia-a-k%C3%B6nyvt%C3%A1rban', 'Biblioterápia a könyvtárban', 'Biblioterápia a könyvtárban', 'A thébai könyvtár bejárata felett az alábbi szöveg állt: „Gyógyír a léleknek!”\r\nA FSZEK szolgáltatása követi a hagyományt és biblioterápiás foglalkozásaival segíti használói szellemi, lelki felfrissülését, személyiségük egészséges fejlődését, életminőségük javítását. Legyen részese az élménynek!', '<h1>Biblioterápia a könyvtárban</h1>\r\n\r\n<p>A thébai könyvtár bejárata felett az alábbi szöveg állt: „Gyógyír a léleknek!”<br>\r\nA FSZEK szolgáltatása követi a hagyományt és biblioterápiás foglalkozásaival segíti használói szellemi, lelki felfrissülését, személyiségük egészséges fejlődését, életminőségük javítását. Legyen részese az élménynek!</p>\r\n\r\n<p> </p>\r\n\r\n<table align=\"right\" border=\"0\" cellpadding=\"0\" cellspacing=\"4\">\r\n <tbody>\r\n  <tr>\r\n   <td><a href=\"http://www.fszek.hu/_Images/konyvtaraink/kozponti_konyvtar/szolgaltatasok/000036141_bt03.jpg.jpg_orig.jpg\"><img alt=\"Fotó: FSZEK\" src=\"http://www.fszek.hu/_Images/konyvtaraink/kozponti_konyvtar/szolgaltatasok/000036141_bt03.jpg.jpg_orig.jpg\" xss=removed></a></td>\r\n  </tr>\r\n  <tr>\r\n   <td>Fotó: FSZEK</td>\r\n  </tr>\r\n </tbody>\r\n</table>\r\n\r\n<p><strong>Gyakorlati tudnivalók</strong>: <br>\r\n<br>\r\nA foglalkozások 17 órakor kezdődnek.<br>\r\nA foglalkozáson való részvétel ingyenes, a részvételi szándékot, kérjük, legkésőbb a foglalkozást megelőző kedd este 20 óráig szíveskedjen jelezni a <a href=\"mailto:biblioterapia@fszek.hu\" target=\"_blank\">biblioterapia@fszek.hu</a>címen.<br>\r\n<br>\r\nA foglalkozásokat <strong>Sóron Ildikó</strong> könyvtáros, biblioterapeuta, a könyvtár munkatársa tartja.<br>\r\nA témával kapcsolatban szintén a <a href=\"mailto:biblioterapia@fszek.hu\" target=\"_blank\">biblioterapia@fszek.hu</a>címen érdeklődhetnek. <br>\r\n<br>\r\n<strong>Kedvcsináló</strong><br>\r\n<br>\r\nA könyvtárban tartott biblioterápiás foglalkozások alapvető célja az <em>önismeret-fejlesztés</em>, valamint különböző kommunikációs és az egyéni életminőség javítását segítő, támogató képességek, készségek fejlesztése.<br>\r\nA sorozat, amely 2010 januárjában indult, a műKINCSvadászat fantázianevet viseli. Az elnevezésben a hangsúly a KINCSen van: az irodalom gazdag tárházából választjuk a terápia alapjául, eszközéül szolgáló irodalmi műkincseket azzal a céllal, hogy a személyiségünkben rejlő egyedi, kis és nagy kincseket azok segítségével felszínre hozhassuk, megmutathassuk mindenkinek. Az általában egy órásra tervezett foglalkozásokat előre meghatározott időpontokban és helyszínen tartjuk, s rendszerint előre ismert a beszélgetés „ugródeszkájául” szolgáló irodalmi mű is. Mindezekről a könyvtár honlapján folyamatosan tájékoztatjuk az érdeklődőket.<br>\r\nA foglalkozások csoportban zajlanak, amelynek ideális létszáma 6-15 fő; az összetétel minden alkalommal változhat, változik (nyitott csoportról van szó). Mindig akadnak újonnan érkezők is a rendszeres látogatók közt.<br>\r\nA foglalkozásokon a művek élményszintű feldolgozása zajlik: az egyén annyit mutat meg magából, amennyit akar. A terapeuta által föltett kérdésekre nem kötelező válaszolni, viszont ha valaki mégis beengedi a többieket is a „személyes rezervátumába”, s esetleg ő is betekint másokéba, akkor megérezheti a közös élmény erejét: a csoportban végzett munka ugyanis energiatöbblettel jár, melynek hatására a „feltépett sebek” gyorsabban gyógyulnak, a gócok intenzívebben semmisülnek meg, és váratlan egyéni felismerések is születhetnek.<br>\r\nA részvétel egyedüli feltétele (a könyvtári regisztráció mellett) a <em>szív és a lélek nyitottsága</em>.<br>\r\nA biblioterápiában használt művek nem pusztán esztétikai értéket képviselnek, hanem magukban hordozzák azt a „tükröt” is, amelybe ha merünk, belenézhetünk, és az ott látottakkal egyetérthetünk vagy vitába szállhatunk. A terápia hatásfoka azoktól függ, akik akkor és ott jelen vannak. A terapeuta kinyitogatja a műben lehetséges üzeneteket rejtő ajtókat, a csoporttag pedig „dönt”: bemegy-e a felkínált, nyitott ajtón, vagy csak bekukucskál. Ha bemegy, állja-e az ott vele szemben lévő tükör „tekintetét”, avagy sem. Azaz vállalja-e az önmagával való szembenézés következményeit, vagy nem. Legtöbbször hat a mű, a beszélgetés észrevétlenül egyre mélyebben rejlő egyéni problémákat érint, fokozódik a belső feszültség, majd a beszélgetés hatására mindez oldódik. Kiengedünk „némi fáradt gőzt”. Lélekben erősebben, testben frissebben térünk vissza a hétköznapokba. Bennünk vérré válik az irodalom! <br>\r\n<br>\r\nA <strong>2017 második félévére</strong> tervezett foglalkozások időpontjai, s az arra az alkalomra választott irodalmi művek listája: <br>\r\n </p>\r\n\r\n<table border=\"0\" cellspacing=\"1\" xss=removed>\r\n <tbody>\r\n  <tr>\r\n   <td xss=removed><strong>Időpontok</strong></td>\r\n   <td xss=removed><strong>A foglalkozás jellege</strong></td>\r\n   <td xss=removed><strong>Alkalmazott irodalmi mű</strong></td>\r\n  </tr>\r\n  <tr>\r\n   <td xss=removed><strong>2017. szeptember 7.</strong></td>\r\n   <td xss=removed>műKINCSvadászat 131.</td>\r\n   <td xss=removed><a href=\"http://www.fszek.hu/konyvtaraink/kozponti_konyvtar/rendezvenyek/?article_hid=36777\" target=\"_self\">Konsztantinosz Kavafisz: Ithaka (vers)</a></td>\r\n  </tr>\r\n  <tr>\r\n   <td xss=removed><strong>2017. szeptember 21.</strong></td>\r\n   <td xss=removed>műKINCSvadászat 132.</td>\r\n   <td xss=removed><a href=\"http://www.fszek.hu/konyvtaraink/kozponti_konyvtar/rendezvenyek/?article_hid=36991\" target=\"_self\">Mészöly Miklós: Jelentés öt egérről (novella)</a></td>\r\n  </tr>\r\n  <tr>\r\n   <td xss=removed><strong>2017. október 5.</strong></td>\r\n   <td xss=removed>műKINCSvadászat 133.</td>\r\n   <td xss=removed><a href=\"http://www.fszek.hu/konyvtaraink/kozponti_konyvtar/rendezvenyek/?article_hid=37118\" target=\"_self\">Nagy László: Hegyi beszéd (vers)</a></td>\r\n  </tr>\r\n  <tr>\r\n   <td xss=removed>